<?php class UserIdentity extends CUserIdentity{	private $_id;	/**	 * Authenticates a user.	 * @return boolean whether authentication succeeds.	 */	public function authenticate()	{		$user=UserModel::model()->find('LOWER(username)=?',array(strtolower($this->username)));		if($user===null)			$this->errorCode=self::ERROR_USERNAME_INVALID;		else if(!$user->validatePassword($this->password))			$this->errorCode=self::ERROR_PASSWORD_INVALID;		else		{			$this->_id=$user->id;			$this->username=$user->username;			$this->setState('nama_lengkap', $user->nama);			$this->setState('email', $user->email);			$this->setState('profile', $user->profile);			$this->setState('access_menu', $user->access_menu);			$this->setState('level', $user->level);			$this->setState('status_user', "user_cms");			$this->setState('route_admin', "cms");			$this->errorCode=self::ERROR_NONE;			$names = InfrastrukturModel::model()->getAttributes(); 			foreach($names as $key => $value)			{				Yii::app()->session[$key."_dbi"] = TRUE;			}		}		return $this->errorCode==self::ERROR_NONE;	}	/**	 * @return integer the ID of the user record	 */	public function getId()	{		return $this->_id;	}}